This is a Perl script that can be used to add IPs to a Linux server.

```
<code linenumbers="off" type="perl">
#!/usr/bin/perl -w
use strict;

########################################
#
# This script is for adding IPs to
# Linux servers.
#
# By Javier Ayala
# 10/14/02
#
########################################

my $checkfile = 1;
my $ipfile    = "NotSet";

sub verifyfile {
    if ( -r "$ipfile" ) {
        print STDOUT "$ipfile: OKn";
        $checkfile = 0;
    }
    else {
        print STDOUT "$ipfile can't be read.  Try again.n";
        $checkfile = 1;
    }
}

print STDOUT "#########################################################n";
print STDOUT "n";
print STDOUT "Welcome to the Additional IP ifcfg File Creator!n";
print STDOUT "n";
print STDOUT "#########################################################nn";
while ( $checkfile != 0 ) {
    print STDOUT "Please tell me where to find the list of IPs: ";
    chomp( $ipfile = <stdin> );
    verifyfile();
}

print STDOUT
"Please tell me what number you would like to begin labeling the files with: ";
chomp( my $filenum = <stdin> );
print STDOUT "n";
print STDOUT "You would like me to get the IP list from: $ipfilen";
print STDOUT "You would like for me to begin numbering with: $filenumn";
print STDOUT "Is this correct? (Y/N): ";
chomp( my $ready = <stdin> );
$ready =~ tr/A-Z/a-z/;

if ( $ready ne "y" ) {
    exit 1;
}

print STDOUT "Adding IPs....n";

# Read in the IPs to add
open( IPFILE, "$ipfile" ) || die "Can't open $ipfilen";
my @ips = <ipfile>;
close IPFILE;

foreach my $ip (@ips) {
    while ( -r "/etc/sysconfig/network-scripts/ifcfg-eth0:$filenum" ) {
        print STDOUT "ifcfg-eth0:$filenum is already in use!n";
        $filenum++;
    }
    chomp($ip);
    open( IFCFG, ">/etc/sysconfig/network-scripts/ifcfg-eth0:$filenum" )
      || die "Can't open file: n";
    print IFCFG "DEVICE=eth0:$filenumn";
    print IFCFG "IPADDR=$ipn";
    print IFCFG "NETMASK=255.255.255.255n";
    print IFCFG "ONBOOT=yesn";
    close IFCFG;
    print STDOUT "Added $ip to ifcfg-eth0:$filenumn";
    $filenum++;
}
print STDOUT "nnDONE!n";
