#!/usr/bin/perl

##########################################################
#
# This script will take a file with a list of domains, 
# and find the IPs that each domain is assigned to.  By 
# default it will remove the trailing "." from each 
# domain name.
#
# Written by Javier Ayala
# Written on 3/29/02
#
##########################################################

print "Enter filename to read from (full path): ";
chomp( $filename = <STDIN> );
print "Enter filename to write to (full path): ";
chomp( $outfile = <STDIN> );
open ( FILE, $filename ) || die "Can't open $filename: n";
open OUTFILE, ">$outfile" || die "Can't open $outfile: n";
@names = <FILE>;

foreach $name (@names) {
	chop($name);
	chop($name);
	($name, $aliases, $addrtype, $length, @addrs) = gethostbyname "$name";
	($a, $b, $c, $d) = unpack('C4', $addrs[0]);
	print OUTFILE "$name = $a.$b.$c.$dn";
        ($name, $aliases, $addrtype, $length, @addrs) = gethostbyname "www.$name";
        ($a, $b, $c, $d) = unpack('C4', $addrs[0]);
        print OUTFILE "www.$name = $a.$b.$c.$dn";
	print OUTFILE "n";
}
close FILE;
close OUTFILE;

#!/usr/bin/perl -w
use strict;
use Net::DNS;

##########################################################
#
# This script will take a list of IPs and find their 
# respective hostnames.
#
# Written by Javier Ayala
# Written on 2/21/07
#
##########################################################

print "Enter filename to read from (full path): ";
chomp( my $filename = <STDIN> );
open ( FILE, $filename ) || die "Can't open $filename: n";
my @ips = <FILE>;

my $res = Net::DNS::Resolver->new;

foreach my $ip (@ips)
{
    chomp $ip;
    my $query = $res->send("$ip");
    if ($query)
    {
        foreach my $rr ($query->answer)
        {
            if ($rr->answer)
            {
              print STDOUT "$ip => n";
              next;
            }
            next unless $rr->type eq "PTR";
            print STDOUT "$ip => ", $rr->string, "n";
        }
    }
    else
    {
        warn "query failed: ", $res->type, "n";
    }
    
    
}

#!/usr/bin/perl -w
use strict;

########################################
#
# This script is for pinging IPs to
# Linux servers.
#
# By Javier Ayala
# 12/31/02
#
########################################

use Net::Ping;

my $protocol = "icmp";

my $checkfile = 1;
my $ipfile = "NotSet";

sub verifyfile {
    if ( -r "$ipfile" ) {
        print STDOUT "$ipfile: OKn";
        $checkfile = 0;
    }
    else {
        print STDOUT "$ipfile can't be read.  Try again.n";
        $checkfile = 1;
    }
}

if ($> != 0) {
    print STDOUT "***Only ROOT can run this script!***n";
    print STDOUT "Either SetUID the script, or run it as root...n";
    exit 1;
}


print STDOUT "#########################################################n";
print STDOUT "n";
print STDOUT "Welcome to the IP Pinger of Glory!n";
print STDOUT "n";
print STDOUT "#########################################################nn";
while ($checkfile != 0) {
    print STDOUT "Please tell me where to find the list of IPs: ";
    chomp($ipfile = <STDIN>);
    verifyfile();
}

print STDOUT "You would like me to get the IP list from: $ipfilen";
print STDOUT "Is this correct? (Y/N): ";
chomp(my $ready = <STDIN>);
$ready =~ tr/A-Z/a-z/;
if ($ready ne "y") {
    exit 1;
}

print STDOUT "Pinging IPs....nn";

# Read in the IPs to add
open (IPFILE, "$ipfile") || die "Can't open $ipfilen";
my @ips = <IPFILE>;
close IPFILE;

foreach my $ip (@ips) {
    chomp($ip);
    my $p = Net::Ping->new($protocol);
    my $result = $p->ping($ip);
    if ($p->ping($ip)) {
        print STDOUT "$ip is Alive!n";
    }
    else {
        print STDOUT "$ip is ***UNRESPONSIVE***n";
    }
    $p->close();
}
print STDOUT "nDONE!n";

#!/usr/bin/perl -w
use strict;

####################################################
#  Script to add users to a system, and create an Apache VirtualHost
#  for them as well.  Be sure to update the script to fit your needs:
#    - @users
#    - VirtualHost Settings
#    - vhosts.conf File Location
#  
#  Written by Javier Ayala
#  Copyright 2006
####################################################

# Modify line below with your users
my @users = ("user1", "user2", "user3", "user4", "user5");

# Create the users with useradd
foreach my $user (@users)
{
    print STDOUT "Running /usr/sbin/useradd -d /home/$user $user";
    system("/usr/sbin/useradd -d /home/$user $user");
    print STDOUT "t[ Done ]n";
}

# Create the website htdocs and cgi-bin directories

foreach my $userdir (@users)
{
    print STDOUT "Creating htdocs and cgi-bin for $userdir";
    system("mkdir -p /home/$userdir/www/htdocs");
    system("chown $userdir:$userdir /home/$userdir/www");
    system("chown $userdir:$userdir /home/$userdir/www/htdocs");
    system("mkdir /home/$userdir/www/cgi-bin");
    system("chown $userdir:$userdir /home/$userdir/www/cgi-bin");
    print STDOUT "t[ Done ]n";
}

# Create the vhost.conf file for Apache
open (CONF, ">/etc/httpd/conf/vhosts.conf") || die "Can't open file: n";
foreach my $site (@users)
{  
    print CONF "<VirtualHost *:80>n";
    print CONF "ServerName $site.comn";
    print CONF "ServerAlias www.$site.comn";
    print CONF "DocumentRoot /home/$site/www/htdocsn";
    print CONF "ScriptAlias /cgi-bin/ /home/$site/www/cgi-bin/n";
    print CONF "<Directory /home/$site/www/cgi-bin>n";
    print CONF "AddHandler application/x-httpd-php .phpn";
    print CONF "</Directory>n";
    print CONF "</VirtualHost>n";
    print CONF "n";
    print STDOUT "Added $site to /etc/httpd/conf/vhosts.confn";
}
close CONF;

#!/usr/bin/perl -w
####################################
#
#        MySQL Backup
#        by Javier Ayala
#        06/26/02
#
####################################
use strict;
use DBI;
use File::Path;
use Sys::Syslog qw(:DEFAULT setlogsock);
use File::NCopy;
use File::List;
use Archive::Tar;
require Compress::Zlib;

# User Definable Variables
my $hostname = 'localhost'; # Hostname of MySQL server
my $user = 'root'; # User to connect to MySQL server.  Should have full admin.
my $pass = '';
my $backupdir = '/root/mysql.back'; # Directory where you want to copy the DB files to.
my $wheretar = '/root/'; # Directory where you want the tarball to be, followed by "/".

#####################################
#
# Nothing below here should need to
# be edited by the common user
#
#####################################

# DB related Variables
my $conn = DBI-&gt;connect("DBI:mysql:host=$hostname", $user, $pass) or die $DBI::errstr;
my $beback = 'FLUSH TABLES with READ LOCK';
my $afback = 'UNLOCK TABLES';

# Begin logging
setlogsock('unix');
openlog('MySQL_BU','pid','user');
syslog('info','MySQL Backup has begun');

# Find dir where DB is located
my $finddata = "SHOW VARIABLES LIKE 'datadir'";
my @datadir = $conn-&gt;selectrow_array($finddata);
syslog('info',"Backing up the following directory: $datadir[1]");

# Define the File::NCopy options
my $dircopy = File::NCopy-&gt;new('recursive' =&gt; 1, 'preserve' =&gt; 1);

# Test for $backupdir, and create if need be
if ( !-d $backupdir) {
mkpath($backupdir,0,755);
syslog('info',"$backupdir does not exist.  Creating...");
}

# Lock the tables, perform the copy, then unlock
syslog('info','Locking Database Tables...');
$conn-&gt;do($beback) or die "Can't lock Tables $DBI::errstr\n";
syslog('info',"Copying $datadir[1] to $backupdir...");
$dircopy-&gt;copy($datadir[1],$backupdir) or die "Can't copy DBs\n";
syslog('info',"Copying of $datadir[1] to $backupdir COMPLETE");
syslog('info','Unlocking Database Tables...');
$conn-&gt;do($afback) or die "Can't unlock Tables $DBI::errstr\n";
$conn-&gt;disconnect;

# Get today's date for the Tarfile's name
my @date = localtime(time);
$date[3] = sprintf("%02d",$date[3] % 100);
$date[4] = sprintf("%02d",($date[4] + 1) % 100);
$date[5] = sprintf("%02d",($date[5] - 100) % 100);
my $tarfile = "$wheretar" . "mysql.back.$date[4]$date[3]$date[5].tgz";

# Create the TAR file of the backed up directory
syslog('info',"Creating $tarfile");

# Get the list of files under $backupdir
my $search = File::List-&gt;new($backupdir);
my @files = @{ $search-&gt;find("") };
Archive::Tar-&gt;create_archive($tarfile,9,@files);
syslog('info','MySQL Backup has completed');
closelog();

